import React, { useEffect, useRef, useState } from 'react';
import Quill from 'quill';
import 'quill/dist/quill.snow.css';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import UsernamePrompt from './UsernamePrompt';
import TypingIndicator from './TypingIndicator';


const SAVE_INTERVAL_MS = 2000;
const TOOLBAR_OPTIONS = [
  [{ header: [1, 2, 3, false] }],
  ['bold', 'italic', 'underline'],
  [{ list: 'ordered' }, { list: 'bullet' }],
  ['image', 'blockquote', 'code-block'],
  ['clean']
];

function TextEditor() {
  const { id: documentId } = useParams();
  const quillRef = useRef();
  const socketRef = useRef();
  const [username, setUsername] = useState(null);
  const [typingUsers, setTypingUsers] = useState([]);

  useEffect(() => {
    if (!username) return;
    socketRef.current = io('http://localhost:5000');
    const quill = new Quill(quillRef.current, {
      theme: 'snow',
      modules: { toolbar: TOOLBAR_OPTIONS }
    });
    quill.disable();
    quill.setText('Loading...');

    socketRef.current.once('load-document', document => {
      quill.setContents(document);
      quill.enable();
    });
    socketRef.current.emit('get-document', documentId);

    const handler = delta => {
      quill.updateContents(delta);
    };
    socketRef.current.on('receive-changes', handler);


    const changeHandler = (delta, oldDelta, source) => {
      if (source !== 'user') return;
      socketRef.current.emit('send-changes', delta);
      socketRef.current.emit('user-typing', username);

  // ...existing code...
    };
    quill.on('text-change', changeHandler);

    socketRef.current.on('users-typing', users => {
      setTypingUsers(users.filter(u => u !== username));
    });

    const saveInterval = setInterval(() => {
      socketRef.current.emit('save-document', quill.getContents());
    }, SAVE_INTERVAL_MS);

    // Remove typing status when user stops typing
    let typingTimeout;
    const handleTypingEnd = () => {
      clearTimeout(typingTimeout);
      typingTimeout = setTimeout(() => {
        socketRef.current.emit('user-stop-typing', username);
      }, 1000);
    };
    quill.on('text-change', handleTypingEnd);

    socketRef.current.emit('set-username', username);

    return () => {
      socketRef.current.disconnect();
      clearInterval(saveInterval);
      clearTimeout(typingTimeout);
    };
  }, [documentId, username]);

  if (!username) {
    return <UsernamePrompt onSubmit={setUsername} />;
  }
  return (
    <div style={{ position: 'relative', height: '100vh' }}>
      <TypingIndicator typingUsers={typingUsers} position="bottom-right" />
      <div ref={quillRef} style={{ height: '100vh' }} />
    </div>
  );
}

export default TextEditor;
