import React from 'react';

function TypingIndicator({ typingUsers }) {
  if (!typingUsers.length) return null;
  return (
    <div style={{ position: 'absolute', top: 10, left: 10, background: '#fffbe6', padding: '0.5rem 1rem', borderRadius: '6px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', zIndex: 10 }}>
      {typingUsers.map((user, idx) => (
        <div key={idx} style={{ fontSize: '1rem', color: '#333' }}>
          <strong>{user}</strong> is typing...
        </div>
      ))}
    </div>
  );
}

export default TypingIndicator;
